  constructor(props) {
    super(props);
    this.state = {date: new Date()};  
  }

  render() {
    <FormLayout>
      <TextField label="First name" onChange={() => {}} />
      <TextField label="Last name" onChange={() => {}} />
      <TextField label="Address" onChange={() => {}} />
      <Select label="Gender" />
      <DatePicker
        month={this.state.month}
        year={this.state.year}
      />

    </FormLayout>
  }
}

export default { Form }
