import React, { useCallback, useState } from 'react';
import {
  Button,
  FormLayout,
  TextField,
  Select,
  DatePicker
} from '@shopify/polaris';

import lunarisData from '../data/lunarisData';

function NewForm() {
  const userData = useState(lunarisData[0])

  const [authUser, setAuthUser] = useState({});
  const [isEditing, setIsEditing] = useState(false);

  const [firstName, setFirstName] = useState(userData[0].first_name);
  const handleFirstNameChange = useCallback((newValue) => setFirstName(newValue), []);


  const [lastName, setLastName] = useState(userData[0].last_name);
  const handleLastNameChange = useCallback((newValue) => {
    setLastName(newValue)
  }, []);

  const [address, setAddress] = useState(userData[0].address);
  const handleAddresssChange = useCallback((newValue) => setAddress(newValue), []);

  const [gender, setGender] = useState(userData[0].gender);
  const handleGenderChange = useCallback((newValue) => setGender(newValue), []);

  const [birthday, setBirthday] = useState(new Date(userData[0].birthday));
  const handleBirthdayChange = useCallback((newValue) => setBirthday(newValue), []);
  const date = birthday.getDate()
  const month = birthday.getMonth()
  const year = birthday.getFullYear()

  const genderOptions = [
    {label: 'Male', value: 'Male'},
    {label: 'Female', value: 'Female'},
    {label: 'Prefer Not To Say', value: 'Prefer Not To Say'},
  ]

  function handleClick() {
    setIsEditing(!isEditing)
  }

  return (
    <FormLayout>
      <TextField
        label="First Name"
        value={firstName}
        onChange={handleFirstNameChange}
        disabled={isEditing}
      />
      <TextField
        label="Last name"
        value={lastName}
        onChange={() => { } }
        disabled={isEditing}
      />
      <TextField
        label="Address"
        value={address}
        onChange={() => { } } 
        disabled={isEditing}
      />
      <Select 
        label="Gender"
        options={genderOptions}
        value={genderOptions.includes(gender) ? "Prefer not to say" : ""}
        onChange={() => {}}
        disabled={isEditing}
      />
      <DatePicker
        date={date}
        month={month}
        year={year}
        disabled={isEditing}
      />
      <Button onClick={handleClick}>Edit</Button>
    </FormLayout>
  );
}

