import React, { useState } from 'react';
import {
  FormLayout,
  TextField,
  Select,
  DatePicker
} from '@shopify/polaris';

import lunarisData from '../data/lunarisData';

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      firstName: "Lunaris",
      lastName: "S.",
      address: "Tokyo, Kichijoji Honcho 1",
      birthday: new Date("2008-02-20"),
      gender: "unknown"
    }
  }

  render() {
    const [{month, year}, setDate] = useState({month: 2, year: 2008});

    const [selectedDates, setSelectedDates] = useState({
      start: new Date('Wed Feb 07 2018 00:00:00 GMT-0500 (EST)'),
      end: new Date('Wed Feb 07 2018 00:00:00 GMT-0500 (EST)'),
    });

    const handleMonthChange = useCallback(
      (month, year) => setDate({month, year}),
      [],
    );

    return (
      <FormLayout>
        <TextField label="First name" onChange={() => { } } />
        <TextField label="Last name" onChange={() => { } } />
        <TextField label="Address" onChange={() => { } } />
        <Select label="Gender" />
        <DatePicker
          month={month}
          year={year}
          onChange={setSelectedDates}
          onMonthChange={handleMonthChange}
          selected={selectedDates} 
        />
      </FormLayout>
    )
  }
}


export default Form
