import React, { useState } from 'react';
import {
  FormLayout,
  TextField,
  Select,
  DatePicker
} from '@shopify/polaris';

import lunarisData from '../data/lunarisData';

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      userInfo: lunarisData,
      birthday: new Date("2008-02-20"),
      isEditing: false
    }
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick() {
    const editStatus = this.setState(prevState => {
      prevState.isEditing = !prevState.isEditing
    })
    return {
      isEditing: editStatus
    }
    console.log(this.state.isEditing)
  }

  render() {
    const date = this.state.birthday.getDate()
    const month = this.state.birthday.getMonth()
    const year = this.state.birthday.getFullYear()

    console.log(date, month, year)

    const options = [
      {label: 'Male', value: 'Male'},
      {label: 'Female', value: 'Female'},
      {label: 'Prefer Not To Say', value: 'Prefer Not To Say'},
    ]

    return (
      <FormLayout>
        <TextField
          disabled={!this.state.isEditing}
          label="First name"
          value={this.state.userInfo[0].first_name}
          onChange={(e) => setFirstname(e.target.value)}
        />
        <TextField
          disabled={!this.state.isEditing}
          label="Last name"
          value={this.state.userInfo[0].last_name}
          onChange={() => { } }
        />
        <TextField
          disabled={!this.state.isEditing}
          label="Address"
          value={this.state.userInfo[0].address}
          onChange={() => { } } />
        <Select 
          disabled={!this.state.isEditing}
          label="Gender"
          options={options}
          onChange={() => {}}
        />
        <DatePicker
          disabled={!this.state.isEditing}
          date={date}
          month={month}
          year={year}
        />
        <button onClick={this.handleClick}>Edit</button>
        <button onClick={() => console.log(this.state.userInfo[0].first_name)}>Click me</button>
      </FormLayout>
    )
  }
}

export default Form
